import React, { createContext, useState, useEffect } from "react";
import { io } from "socket.io-client";
import axios from "axios";

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(() => {
    // Load user from localStorage on refresh
    const storedUser = localStorage.getItem("user");
    return storedUser ? JSON.parse(storedUser) : null;
  });
  const [token, setToken] = useState(() => localStorage.getItem("token") || null);
  const [socket, setSocket] = useState(null);

  // Connect to WebSocket whenever token changes
  useEffect(() => {
    if (token) {
      const newSocket = io("http://localhost:5000", {
        auth: { token }, // optional if your backend checks it
      });

      newSocket.on("connect", () => {
        console.log("Connected to WebSocket:", newSocket.id);
      });

      newSocket.on("disconnect", () => {
        console.log("Disconnected from WebSocket");
      });

      // Example event: guest status updated
      newSocket.on("guestUpdated", (updatedGuest) => {
        console.log("Guest updated:", updatedGuest);
        // Optional: update local state if you store guests here
      });

      setSocket(newSocket);

      return () => {
        newSocket.disconnect();
      };
    }
  }, [token]);

  const login = async (token, userData) => {
    setToken(token);
    setUser(userData);
    localStorage.setItem("token", token);
    localStorage.setItem("user", JSON.stringify(userData));
    axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;
  };

  const logout = () => {
    setToken(null);
    setUser(null);
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    if (socket) socket.disconnect();
  };

  const register = async (token, userData) => {
    // Same as login after registration
    await login(token, userData);
  };

  return (
    <AuthContext.Provider value={{ user, token, login, logout, register, socket }}>
      {children}
    </AuthContext.Provider>
  );
};
